theorem P2025HMMTfeb_comb_2 :
  let grid := Finset.product (Finset.range 3) (Finset.range 3)
  let start := (0, 0)
  let home := (2, 2)
  let valid_removals := {s : Finset (ℕ × ℕ) | s ⊆ grid ∧ s.card = 4 ∧ start ∉ s ∧ home ∉ s ∧
    ¬ ∃ (p : List (ℕ × ℕ)), p.Chain' (fun a b => (a.1 = b.1 ∧ (a.2 - b.2)^2 = 1) ∨ 
                                    (a.2 = b.2 ∧ (a.1 - b.1)^2 = 1)) ∧
    p.head? = some start ∧ p.getLast? = some home ∧ ∀ x ∈ p, x ∈ grid \ s}
  Set.ncard valid_removals = (8 : ℕ) := by

theorem P2024HMMTnov_team_7 :
  let grids := (Fin 7 → Bool) × (Fin 7 → Bool)
  let valid (g : grids) : Prop := 
    let (v, h) := g
    let cuts : Set (Fin 8 × Fin 8) := 
      {x | (∃ i, x.1 = i ∧ v i) ∨ (∃ j, x.2 = j ∧ h j)}
    let coloring (x : Fin 8 × Fin 8) : Bool := 
      ((x.1 + x.2 + if x ∈ cuts then 0 else 1) % 2 = 0)
    let black_area := Finset.card (Finset.univ.filter (fun x => coloring x))
    let white_area := 64 - black_area
    black_area = white_area
  Fintype.card {g : grids // valid g} = (64 : ℕ) := by
